import React, { PureComponent } from 'react'

import axios from 'axios'

export default class App extends PureComponent {
  constructor (props) {
    super(props)

  }

  async componentDidMount() {

    // axios发送基本的网络请求
    axios({
      url: "http://httpbin.org/get",
      params: {
        name: "why",
        age: "18"
      },
      method: "GET"
    }).then(res => {
      console.log(res);
    })

    try {
      const result = axios({
        url: "http://httpbin.org/post",
        data: {
          name: "why",
          age: "18"
        },
        method: "POST"
      })

      console.log(result);
    } catch (error) {
      console.log(error);
    }

    // axios.all
    const request1 = axios({
      url: "http://httpbin.org/get",
      params: {
        name: "why",
        age: "18"
      }
    })

    const request2 = axios({
      url: "http://httpbin.org/post",
      data: {
        name: "why",
        age: "18"
      },
      method: "POST"
    })

    axios.all([request1, request2]).then(res => {
      console.log(res);
    }).catch(err => console.log(err))
  }

  render() {
    return (
      <div>
        App
      </div>
    )
  }
}
