import React, { PureComponent } from 'react'

import axios from 'axios'

export default class App extends PureComponent {
  constructor (props) {
    super(props)

  }

  componentDidMount() {

    // axios发送基本的网络请求
    axios({
      url: "http://httpbin.org/get",
      params: {
        name: "why",
        age: "18"
      },
      method: "GET"
    }).then(res => {
      console.log(res);
    })

    axios({
      url: "http://httpbin.org/post",
      data: {
        name: "why",
        age: "18"
      },
      method: "POST"
    }).then(res => {
      console.log("POST", res);
    })
  }

  render() {
    return (
      <div>
        App
      </div>
    )
  }
}
