import React, { PureComponent } from 'react'

import { EditOutlined, EllipsisOutlined, SettingOutlined, AntDesignOutlined } from '@ant-design/icons';
import { Avatar, Card } from 'antd';

import { CSSTransition } from 'react-transition-group'


const { Meta } = Card;


export default class CSSTransitionDome extends PureComponent {
  render() {
    return (
      <div>
        <Avatar
          size={{ xs: 24, sm: 32, md: 40, lg: 64, xl: 80, xxl: 100 }}
          icon={<AntDesignOutlined />}
        />
        <CSSTransition>
          <Card
            style={{ width: 300 }}
            cover={
              <img
                alt="example"
                src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
              />
            }
            actions={[
              <SettingOutlined key="setting" />,
              <EditOutlined key="edit" />,
              <EllipsisOutlined key="ellipsis" />,
            ]}
          >
            <Meta style={{ "width": "100px", "height": "100px" }}
              avatar={
                <Avatar src="https://upload.jianshu.io/users/upload_avatars/1102036/c3628b478f06.jpeg?imageMogr2/auto-orient/strip|imageView2/1/w/144/h/144/format/webp"
                  size={{ xs: 24, sm: 32, md: 40, lg: 64, xl: 80, xxl: 100 }} />
              }
              title="Card title"
              description="This is the description"
            />
          </Card>
        </CSSTransition>
        <button>显示/隐藏</button>
      </div>
    )
  }
}
