import {
  ADD_NUMBER,
  SUB_NUMBER,
  INCRETMENT,
  DECRETMENT,
  CHANGE_BANNER,
  CHANGE_RECOMMEND
} from './constants.js'

export const addAction = num => ({
  type: ADD_NUMBER,
  num
})

export const subAction = num => ({
  type: SUB_NUMBER,
  num
})

export const incAction = () => ({
  type: INCRETMENT
})

export const decAction = () => ({
  type: DECRETMENT
})

export const changeBannerAction = (banners) => ({
  type: CHANGE_BANNER,
  banners
})

export const changeRecommendAction = recommends => ({
  type: CHANGE_RECOMMEND,
  recommends
})


// 利用redux-thunk 定义actions函数 到中间件 middleware
export const getHomeMultidataAction = dispatch => {
  console.log("action函数中");
}
