import React, { PureComponent } from 'react'

import {
  Route,
  Redirect
} from 'react-router-dom'

export default class User extends PureComponent {
  constructor (props) {
    super(props)

    this.state = {
      isLogin: false
    }
  }

  render() {
    return this.state.isLogin ? (
      <div>
        <h2>User</h2>
        <h2>用户名: coderwhy</h2>
      </div>
    ) : <Route path="/user" render={() => <Redirect to="/login" />} />
  }
}
