import React, { PureComponent } from 'react'

import {
  NavLink,
  Routes,
  Route
} from 'react-router-dom'

function AboutHisotry() {
  return (
    <h2>企业成立于2000年</h2>
  )
}

function AboutCulture() {
  return (
    <h2>企业成立于2000年</h2>
  )
}

function Aboutcontact() {
  return (
    <h2>企业成立于2000年</h2>
  )
}

export default class About extends PureComponent {
  render() {
    return (
      <div>
        <h2>About</h2>
        <NavLink to="/about" className={({ isActive }) => isActive ? "about-isActive" : undefined}>企业历史</NavLink>
        <NavLink to="/about/culture" className={({ isActive }) => isActive ? "about-isActive" : undefined}>企业文化</NavLink>
        <NavLink to="/about/contact" className={({ isActive }) => isActive ? "about-isActive" : undefined}>联系我们</NavLink>

        <Routes>
          <Route path='/about/*' element={<AboutHisotry />} />
          <Route path='/about/culture' element={<AboutCulture />} />
          <Route path='/about/contact' element={<Aboutcontact />} />
        </Routes>
      </div>
    )
  }
}
