import React, { useState } from 'react'

export default function MemoHookDemo01() {
  const [count, setcount] = useState(10)

  let total = 0

  for (const i = 1; i <= count; i++) {
    total += i
  }

  return (
    <div>
      <h2>MemoHookDemo01</h2>
      <h2>计算数字的和: {count}</h2>
    </div>
  )
}
