import React, { useState } from 'react'

export default function CustomDataStoreHook() {
  const [name, setName] = useState(() => {
    const name = localStorage.getItem("name")
    return name
  })

  return (
    <div>
      <h2>CustomDataStoreHook</h2>
    </div>
  )
}
