import React, { useRef } from 'react'

const HYInput = () => {
  return <input type="text" />
}

export default function ForwardRefDemo() {
  const inputRef = useRef()

  return (
    <div>
      <HYInput ref={inputRef} />
      <button>聚焦</button>
    </div>
  )
}
