import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { getbanner } from '../services/recommend';

// 网络请求中间件 createAsyncThunk(类型, 回调)
export const fetchBannerDataAction = createAsyncThunk("banners", async (arg) => {
  console.log(456);
  const result = await getbanner()

  return result.data
})

const recommendSlice = createSlice({
  name: "recommend",
  initialState: {
    banners: []
  },
  reducers: {

  }
})


// export const { } = recommendSlice.actions
export default recommendSlice.reducer
